import { Component, OnInit } from '@angular/core';
import { BookmarkedJobService } from 'src/app/bookmarked-job.service';
import { BookmarkedJob } from 'src/app/jobservice.service';

@Component({
  selector: 'app-remove-bookmark',
  templateUrl: './remove-bookmark.component.html',
  styleUrls: ['./remove-bookmark.component.css']
})
export class RemoveBookmarkComponent implements OnInit {

  constructor(private bookmarkService:BookmarkedJobService) { }

  ngOnInit(): void {
  }
  onRemove(){ 
    console.log("message")
    this.bookmarkService.removeBookmark(this.bookmarkedJob).subscribe(data =>{
      alert(data)
    })

  }
  bookmarkedJob: any = {
    skill: {
      name: "",
      description: ""

    },
    job: {
      skill: "",
      postedDate: "",
      awardedTo: "",
      active: "",
      jobApplications: [],

    },
    id:""

  }

}
<!DOCTYPE html>
<html lang="en">
<head>
    <title>Remove Bookmark</title>


    <link rel="stylesheet" type="text/css" href="remove-bookmark.component.css">  
</head>
<body>

  <h1>Remove Bookmark</h1>

  <div class="container">
    <!-- <img src="assets/JobImg.jpg" alt="" class="center"> -->
    <!-- form to enter details of site -->
    <form #bookmarkForm= "ngForm" (ngSubmit)="onRemove()">
      <div class="input-field">
        <label for="site_name">Enter ID</label>
        <input name="site_name" type="text" 
               placeholder="Enter ID To Remove Bookmark" [(ngModel)]="bookmarkedJob.id" required>
      </div>
    <div class="input-field">
      <input type="submit" value="Remove" class="btn btn-primary">
    </div>



    </form>

    <!-- section where bookmarks will be displayed   -->


    <div class="bookmarks"></div>
  </div>

</body>
</html>
